#ifndef _mma_cfg_h
#define _mma_cfg_h

#define MMA_CFG_REQUEST_QUEUE_SIZE  10   /* static array */
#define MMA_IF_REQUEST_QUEUE_SIZE   3    /* circular queue (at most 3 requests: MEA/SMA, MMC, MMA) */

/* sending configuration */
#define MMA_CFG_SENDING_QUEUE_SIZE  1    /* static array: enable background sending by makeing the value > 1 */

#if MMA_CFG_SENDING_QUEUE_SIZE > 1
#define MMA_CFG_SENDING_RETRY_TIME  3
#define MMA_CFG_SENDING_RETRY_INTERVAL  10  /* second */
#define MMA_CFG_SENDING_RETRY_INTERVAL_INCREASE  1  /* if true(=1), say MMA_CFG_SENDING_RETRY_INTERVAL as 10, 
                                                       the retry intervals would be like 10, 20, 30; otherwise, 
                                                       it would be 10, 10, 10 */
#else
#define MMA_CFG_SENDING_RETRY_TIME  0
#define MMA_CFG_SENDING_RETRY_INTERVAL  0
#define MMA_CFG_SENDING_RETRY_INTERVAL_INCREASE  0
#endif

#define MMA_CFG_MMS_XML_FOLDER  MMS_FOLDER
#define MMA_CFG_MMS_XML_PREFIX  'X'
#define MMA_GET_MMS_XML_FILEPATH(x, d)   sprintf(x, "%s%c%02d.xml", MMA_CFG_MMS_XML_FOLDER, MMA_CFG_MMS_XML_PREFIX, d);
#define MMA_GET_MMS_TEMPLATE_TEXT_FILEPATH(x)   sprintf(x, "%stemplate.txt", MMA_CFG_MMS_XML_FOLDER);

#endif /* _mma_cfg_h */
